mapper.select:
SELECT
    m.*
FROM
    xms_msg m
LEFT JOIN xms_msgrecycle mr ON m.id = mr.msgid
WHERE
    m.mail = #{mail}

    AND m.type=#{type}


    AND m.create_time >= #{startDate}


    AND m.create_time <= #{endDate}


AND mr.id IS NULL
LIMIT #{offset}, #{limit};

        SELECT
            m.*
        FROM
            xms_msg m
        LEFT JOIN xms_msgrecycle mr ON m.id = mr.msgid
        WHERE
            m.mail = #{mail}

            AND m.type=#{type}


            AND m.create_time >= #{startDate}


            AND m.create_time <= #{endDate}

        AND mr.isread=1
		AND mr.isdelete=0
		LIMIT #{offset}, #{limit};
